        bool reco_FD_Neutrons_2p = (Enable_FD_neutrons || (NeutronsFD_ind.size() == 0)); // no id. FD neutrons for Enable_FD_neutrons = false
        bool two_protons_2p = (Protons_ind.size() == 2);
